import gql from 'graphql-tag'
import {useQuery} from '@apollo/react-hooks'
import {Card, ResourceList, Stack, TextStyle, Thumbnail} from '@shopify/polaris'
import store from 'store-js'
import React from 'react'

const GET_PRODUCTS_BY_ID = gql`
    query getProducts($ids:[ID!]!){
    nodes(ids: $ids){
      ... on Product{
        title
        handle
        id
        images(first:1){
          edges{
            node{
              originalSrc
              altText
            }
          }
        }
        variants(first:1){
          edges{
            node{
              price
              id
            }
          }
        }
      }
    }
  }
`

const ProductList = () => {
    const {loading, err, data } = useQuery(GET_PRODUCTS_BY_ID,{variables:{ids:store.get('ids')}});
    if(loading) return <div>Loading...</div>
    if (err) return <div>{err.message}</div>
    console.log("this is the data " + data.nodes.title)
    return (
    <div>
        <Card>
            <ResourceList 
                showHeader
                resourceName={{singular:"Product",plural:"Products"}}
                items={data.nodes}
                renderItem={item => {
                    const media = (
                        <Thumbnail 
                            source={
                                item.images.edges[0] ? item.images.edges[0].node.originalSrc : ''
                            }
                            alt={item.images.edges[0] ? item.images.edges[0].altText : ''}
                        />
                    )
                }}
            />
            {data.nodes.map(product =>(
                <div key={product.id}>{product.title}</div>
            ))}
        </Card>
    </div>  
    )
    }


export default ProductList
