    return (
        <div>
            current script tags
        {data.scriptTags.edges.map(tag =>{
            return(
                <div key={tag.node.id}>
                    <p>{tag.node.id}</p>
                </div>
            )
        })}
        </div>
    )
}

export default ScriptPage
